import React, { useEffect, useState } from "react";

const CONFIG = {
  APP_ID: "1187395139713463",
  CONFIG_ID: "2282335175549472",
  BACKEND_URL: "http://localhost:8081",
  REDIRECT_URI: "https://c6c766eee161.ngrok-free.app",
};

export default function App() {
  const [isSDKLoaded, setIsSDKLoaded] = useState(false);
  const [status, setStatus] = useState("idle");
  const [message, setMessage] = useState("");
  const [businessInfo, setBusinessInfo] = useState(null);
  const [nextSteps, setNextSteps] = useState([]);
  const [loadingMethod, setLoadingMethod] = useState("all_js");

  // Method 1: URL Parameters + Manual Init (Most Reliable)
  const loadSDKWithURLParams = () => {
    console.log("üîÑ Loading Facebook SDK with URL parameters method...");

    // Remove existing script if present
    const existingScript = document.getElementById("facebook-jssdk");
    if (existingScript) existingScript.remove();

    // Create script with all parameters in URL
    const script = document.createElement("script");
    script.id = "facebook-jssdk";
    script.async = true;
    script.defer = true;
    script.crossOrigin = "anonymous";
    script.src = `https://connect.facebook.net/en_US/sdk.js#xfbml=1&version=v19.0&appId=${CONFIG.APP_ID}&autoLogAppEvents=1`;

    script.onload = () => {
      console.log("‚úÖ Facebook SDK loaded with URL params");

      // Even with URL params, we need to manually init for FB.login to work
      setTimeout(() => {
        if (window.FB) {
          console.log("üîß Manually initializing FB SDK for login functionality...");
          try {
            // Manual initialization even though URL has params
            window.FB.init({
              appId: CONFIG.APP_ID,
              cookie: true,
              xfbml: true,
              version: 'v19.0'
            });

            // Test that it's working
            window.FB.getLoginStatus(function(response) {
              console.log("‚úÖ Manual init successful, status:", response.status);
              setIsSDKLoaded(true);
            });

          } catch (error) {
            console.error("‚ùå Manual init failed:", error);
            setMessage(`Manual init failed: ${error.message}`);
          }
        } else {
          console.error("‚ùå FB object not available after load");
          setMessage("Facebook SDK loaded but FB object not available");
        }
      }, 1500); // Give it more time
    };

    script.onerror = (error) => {
      console.error("‚ùå Failed to load Facebook SDK:", error);
      setMessage("Failed to load Facebook SDK");
    };

    document.head.appendChild(script);
  };

  // Method 2: Traditional fbAsyncInit with tested version
  const loadSDKWithAsyncInit = () => {
    console.log("üîÑ Loading Facebook SDK with fbAsyncInit method...");

    // Set up fbAsyncInit before loading script
    window.fbAsyncInit = function() {
      console.log("fbAsyncInit called");
      try {
        window.FB.init({
          appId: CONFIG.APP_ID,
          cookie: true,
          xfbml: true,
          version: 'v19.0' // Try v19.0 which is more likely to be supported
        });

        console.log("FB.init completed, testing...");
        window.FB.getLoginStatus(function(response) {
          console.log("SDK test successful:", response.status);
          setIsSDKLoaded(true);
        });
      } catch (error) {
        console.error("‚ùå FB.init error:", error);
        setMessage(`FB.init error: ${error.message}`);
      }
    };

    // Remove existing script
    const existingScript = document.getElementById("facebook-jssdk");
    if (existingScript) existingScript.remove();

    // Load script
    const script = document.createElement("script");
    script.id = "facebook-jssdk";
    script.async = true;
    script.src = "https://connect.facebook.net/en_US/sdk.js";

    script.onload = () => console.log("‚úÖ FB script loaded, waiting for init...");
    script.onerror = (error) => {
      console.error("‚ùå Script load error:", error);
      setMessage("Script loading failed");
    };

    document.head.appendChild(script);
  };

  // Method 3: Load with older version (fallback)
  const loadSDKWithOlderVersion = () => {
    console.log("Loading Facebook SDK with older version...");

    window.fbAsyncInit = function() {
      console.log("fbAsyncInit called (older version)");
      try {
        window.FB.init({
          appId: CONFIG.APP_ID,
          cookie: true,
          xfbml: true,
          version: 'v17.0' // Older, more stable version
        });

        window.FB.getLoginStatus(function(response) {
          console.log("Older version SDK test:", response.status);
          setIsSDKLoaded(true);
        });
      } catch (error) {
        console.error("Older version error:", error);
        setMessage(`Older version error: ${error.message}`);
      }
    };

    const existingScript = document.getElementById("facebook-jssdk");
    if (existingScript) existingScript.remove();

    const script = document.createElement("script");
    script.id = "facebook-jssdk";
    script.async = true;
    script.src = "https://connect.facebook.net/en_US/sdk.js";

    script.onload = () => console.log("Older version script loaded");
    script.onerror = (error) => console.error("Older version script error:", error);

    document.head.appendChild(script);
  };

  // Method 4: Using all.js instead of sdk.js (Stack Overflow solution)
  const loadSDKWithAllJS = () => {
    console.log("üîÑ Loading Facebook SDK with all.js method...");

    window.fbAsyncInit = function() {
      console.log("fbAsyncInit called (all.js method)");
      try {
        window.FB.init({
          appId: CONFIG.APP_ID,
          cookie: true,
          xfbml: true,
          version: 'v19.0'
        });

        window.FB.getLoginStatus(function(response) {
          console.log("all.js SDK test:", response.status);
          setIsSDKLoaded(true);
        });
      } catch (error) {
        console.error("‚ùå all.js method error:", error);
        setMessage(`all.js method error: ${error.message}`);
      }
    };

    const existingScript = document.getElementById("facebook-jssdk");
    if (existingScript) existingScript.remove();

    const script = document.createElement("script");
    script.id = "facebook-jssdk";
    script.async = true;
    script.src = "https://connect.facebook.net/en_US/all.js"; // Using all.js instead of sdk.js

    script.onload = () => console.log("‚úÖ all.js script loaded");
    script.onerror = (error) => console.error("‚ùå all.js script error:", error);

    document.head.appendChild(script);
  };

  // Method 5: No version specified (let Facebook decide)
  const loadSDKWithNoVersion = () => {
    console.log("üîÑ Loading Facebook SDK with no version specified...");

    window.fbAsyncInit = function() {
      console.log("fbAsyncInit called (no version)");
      try {
        window.FB.init({
          appId: CONFIG.APP_ID,
          cookie: true,
          xfbml: true
          // No version specified - let Facebook use default
        });

        window.FB.getLoginStatus(function(response) {
          console.log("No version SDK test:", response.status);
          setIsSDKLoaded(true);
        });
      } catch (error) {
        console.error("‚ùå No version error:", error);
        setMessage(`No version error: ${error.message}`);
      }
    };

    const existingScript = document.getElementById("facebook-jssdk");
    if (existingScript) existingScript.remove();

    const script = document.createElement("script");
    script.id = "facebook-jssdk";
    script.async = true;
    script.src = "https://connect.facebook.net/en_US/sdk.js";

    script.onload = () => console.log("‚úÖ No version script loaded");
    script.onerror = (error) => console.error("‚ùå No version script error:", error);

    document.head.appendChild(script);
  };

  // Initialize SDK on component mount
  useEffect(() => {
    // Ensure fb-root div exists
    if (!document.getElementById("fb-root")) {
      const fbRoot = document.createElement("div");
      fbRoot.id = "fb-root";
      document.body.insertBefore(fbRoot, document.body.firstChild);
      console.log("‚úÖ Added fb-root div");
    }

    // Start with the most reliable method
    if (loadingMethod === "url_params") {
      loadSDKWithURLParams();
    } else if (loadingMethod === "async_init") {
      loadSDKWithAsyncInit();
    } else if (loadingMethod === "older_version") {
      loadSDKWithOlderVersion();
    } else if (loadingMethod === "all_js") {
      loadSDKWithAllJS();
    } else if (loadingMethod === "no_version") {
      loadSDKWithNoVersion();
    }

    // Cleanup timeout
    const timeout = setTimeout(() => {
      if (!isSDKLoaded) {
        console.warn("‚ö†Ô∏è SDK loading timeout");
        setMessage("SDK loading timed out. Try a different method.");
      }
    }, 10000);

    return () => clearTimeout(timeout);
  }, [loadingMethod, isSDKLoaded]);

  // Listen for Facebook messages
  useEffect(() => {
    const messageListener = (event) => {
      if (!event.origin.endsWith('.facebook.com') && 
          event.origin !== 'https://www.facebook.com' && 
          event.origin !== 'https://business.facebook.com') {
        return;
      }

      try {
        const data = typeof event.data === "string" ? JSON.parse(event.data) : event.data;
        if (data?.type === "WA_EMBEDDED_SIGNUP") {
          console.log("üì® WhatsApp Embedded Signup event:", data);

          if (data.event === "FINISH") {
            console.log("‚úÖ Signup completed");
          } else if (data.event === "CANCEL") {
            setStatus("error");
            setMessage(`Signup cancelled: ${data.data?.current_step || 'unknown'}`);
          }
        }
      } catch (e) {
        // Ignore non-JSON messages
      }
    };

    window.addEventListener("message", messageListener);
    return () => window.removeEventListener("message", messageListener);
  }, []);

  const startSignup = () => {
    if (!isSDKLoaded || !window.FB) {
      setMessage("Facebook SDK not ready");
      return;
    }

    setStatus("loading");
    setMessage("Starting WhatsApp Business signup...");

    console.log("Starting FB.login with embedded signup");
    console.log("FB object methods:", Object.keys(window.FB));
    console.log("FB.login available:", typeof window.FB.login);

    // First, let's check FB status
    window.FB.getLoginStatus(function(statusResponse) {
      console.log("Current FB status before login:", statusResponse);

      try {
        const loginConfig = {
          config_id: CONFIG.CONFIG_ID,
          response_type: "code",
          override_default_response_type: true,
          extras: {
            setup: {},
            featureType: '',
            sessionInfoVersion: '3'
          }
        };

        console.log("FB.login config:", loginConfig);

        window.FB.login(
          (response) => {
            console.log("FB.login response:", response);

            if (response.error) {
              setStatus("error");
              setMessage(`Login error: ${response.error.message}`);
              return;
            }

            const code = response?.authResponse?.code;
            if (code) {
              console.log("Got authorization code");
              setupBusinessAccount(code);
            } else {
              setStatus("error");
              setMessage(`No code received. Status: ${response.status}`);
            }
          },
          loginConfig
        );
      } catch (error) {
        console.error("FB.login error:", error);
        console.log("Error details:", {
          name: error.name,
          message: error.message,
          stack: error.stack
        });
        setStatus("error");
        setMessage(`FB.login failed: ${error.message}`);
      }
    });
  };

  const setupBusinessAccount = async (authCode) => {
    try {
      setMessage("Processing WhatsApp Business setup...");

      const res = await fetch(`${CONFIG.BACKEND_URL}/api/whatsapp/setup`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          authorization_code: authCode,
          redirect_uri: CONFIG.REDIRECT_URI,
          client_info: {
            user_agent: navigator.userAgent,
            timestamp: new Date().toISOString()
          },
        }),
      });

      const result = await res.json();

      if (result.success) {
        setStatus("success");
        setMessage(result.message || "Setup completed!");
        setBusinessInfo(result.business_info);
        setNextSteps(result.next_steps || []);
      } else {
        throw new Error(result.error || "Setup failed");
      }
    } catch (err) {
      setStatus("error");
      setMessage(`Setup error: ${err.message}`);
    }
  };

  const reset = () => {
    setStatus("idle");
    setMessage("");
    setBusinessInfo(null);
    setNextSteps([]);
  };

  const tryDifferentMethod = (method) => {
    setIsSDKLoaded(false);
    setMessage("");
    setStatus("idle");
    setLoadingMethod(method);
  };

  const testConnection = () => {
    if (window.FB) {
      window.FB.getLoginStatus((response) => {
        alert(`FB Status: ${response.status}\nSDK Working: ${!!window.FB}`);
      });
    } else {
      alert("FB object not available");
    }
  };

  return (
    <div style={{ maxWidth: 720, margin: "40px auto", fontFamily: "system-ui", padding: "20px" }}>
      {/* Ensure fb-root div exists */}
      <div id="fb-root"></div>

      <h1>WhatsApp Business ‚Äî Embedded Signup</h1>

      {/* Loading Method Controls */}
      <div style={{ 
        marginBottom: "20px", 
        padding: "15px", 
        backgroundColor: "#f8f9fa", 
        borderRadius: "8px",
        border: "1px solid #e9ecef"
      }}>
        <strong>SDK Loading Method:</strong>
        <div style={{ marginTop: "8px" }}>
          <button 
            onClick={() => tryDifferentMethod("url_params")} 
            style={{ 
              marginRight: "8px", 
              padding: "6px 12px", 
              backgroundColor: loadingMethod === "url_params" ? "#007bff" : "#6c757d",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer"
            }}
          >
            URL Params (Recommended)
          </button>
          <button 
            onClick={() => tryDifferentMethod("older_version")} 
            style={{ 
              marginRight: "8px", 
              marginBottom: "4px",
              padding: "6px 12px", 
              backgroundColor: loadingMethod === "older_version" ? "#007bff" : "#6c757d",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer"
            }}
          >
            v17.0 Fallback
          </button>
          <button 
            onClick={() => tryDifferentMethod("all_js")} 
            style={{ 
              marginRight: "8px", 
              marginBottom: "4px",
              padding: "6px 12px", 
              backgroundColor: loadingMethod === "all_js" ? "#007bff" : "#6c757d",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer"
            }}
          >
            all.js Method
          </button>
          <button 
            onClick={() => tryDifferentMethod("no_version")} 
            style={{ 
              marginRight: "8px", 
              marginBottom: "4px",
              padding: "6px 12px", 
              backgroundColor: loadingMethod === "no_version" ? "#007bff" : "#6c757d",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer"
            }}
          >
            No Version
          </button>
          <button 
            onClick={testConnection}
            style={{ 
              padding: "6px 12px", 
              backgroundColor: "#28a745",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer"
            }}
          >
            Test Connection
          </button>
        </div>
      </div>

      {/* Status Display */}
      <div style={{ 
        marginBottom: "20px", 
        padding: "10px", 
        backgroundColor: isSDKLoaded ? "#d4edda" : "#fff3cd",
        border: `1px solid ${isSDKLoaded ? "#c3e6cb" : "#ffeaa7"}`,
        borderRadius: "4px",
        fontSize: "14px"
      }}>
        <strong>Status:</strong> {isSDKLoaded ? "‚úÖ SDK Ready" : "‚è≥ Loading..."}<br/>
        <strong>Method:</strong> {loadingMethod}<br/>
        <strong>FB Object:</strong> {window.FB ? "‚úÖ" : "‚ùå"}<br/>
        <strong>App ID:</strong> {CONFIG.APP_ID}
        {message && <div style={{ marginTop: "8px", color: "#856404" }}><strong>Message:</strong> {message}</div>}
      </div>

      {status === "idle" && (
        <button
          onClick={startSignup}
          disabled={!isSDKLoaded}
          style={{
            padding: "12px 24px",
            fontSize: "16px",
            fontWeight: "600",
            backgroundColor: isSDKLoaded ? "#25d366" : "#ccc",
            color: "white",
            border: "none",
            borderRadius: "8px",
            cursor: isSDKLoaded ? "pointer" : "not-allowed"
          }}
        >
          {isSDKLoaded ? "üöÄ Connect WhatsApp Business" : "‚è≥ Loading SDK..."}
        </button>
      )}

      {status === "loading" && (
        <div>
          <h3>‚è≥ Setting up...</h3>
          <p>{message}</p>
        </div>
      )}

      {status === "success" && (
        <div>
          <h3 style={{ color: "#28a745" }}>‚úÖ Setup Complete!</h3>
          <p>{message}</p>

          {businessInfo && (
            <div style={{ 
              marginTop: "16px", 
              padding: "16px", 
              backgroundColor: "#d4edda", 
              borderRadius: "8px" 
            }}>
              <div><strong>Business:</strong> {businessInfo.business_name}</div>
              <div><strong>WABA ID:</strong> {businessInfo.waba_id}</div>
              <div><strong>Phone Numbers:</strong> {businessInfo.phone_numbers?.length}</div>
              <div><strong>Webhooks:</strong> {businessInfo.webhooks_enabled ? "‚úÖ" : "‚ùå"}</div>
            </div>
          )}

          {nextSteps.length > 0 && (
            <div style={{ marginTop: "16px" }}>
              <h4>Next Steps:</h4>
              <ul>
                {nextSteps.map((step, i) => (
                  <li key={i}>{step}</li>
                ))}
              </ul>
            </div>
          )}

          <button onClick={reset} style={{ marginTop: "16px", padding: "8px 16px" }}>
            üîÑ Setup Another Account
          </button>
        </div>
      )}

      {status === "error" && (
        <div>
          <h3 style={{ color: "#dc3545" }}>‚ùå Setup Failed</h3>
          <div style={{ 
            padding: "12px", 
            backgroundColor: "#f8d7da", 
            color: "#721c24",
            borderRadius: "6px",
            marginBottom: "16px"
          }}>
            {message}
          </div>
          <button onClick={reset} style={{ padding: "8px 16px" }}>
            üîÑ Try Again
          </button>
        </div>
      )}
    </div>
  );
}
