import React, { useEffect, useState, useCallback } from "react";

const CONFIG = {
  APP_ID: "1187395139713463",
  API_VERSION: "v23.0",
  CONFIG_ID: "2282335175549472",
  BACKEND_URL: "http://localhost:8081",
  REDIRECT_URI: "https://c6c766eee161.ngrok-free.app",
};

export default function App() {
  const [isSDKLoaded, setIsSDKLoaded] = useState(false);
  const [sdkError, setSdkError] = useState(null);
  const [status, setStatus] = useState("idle");
  const [message, setMessage] = useState("");
  const [businessInfo, setBusinessInfo] = useState(null);
  const [nextSteps, setNextSteps] = useState([]);
  const [appValidation, setAppValidation] = useState(null);

  // Validate Facebook App ID
  const validateAppId = useCallback(async () => {
    try {
      console.log("Validating Facebook App ID...");
      const response = await fetch(`https://graph.facebook.com/v23.0/${CONFIG.APP_ID}?fields=id,name,link`);
      const data = await response.json();

      if (response.ok && data.id) {
        console.log("‚úÖ App ID is valid:", data);
        setAppValidation({ valid: true, data });
      } else {
        console.error("‚ùå App ID validation failed:", data);
        setAppValidation({ valid: false, error: data.error?.message || "App ID not found" });
        setSdkError(`Invalid App ID: ${data.error?.message || "App not found"}`);
      }
    } catch (error) {
      console.error("‚ùå App validation network error:", error);
      setAppValidation({ valid: false, error: "Network error during validation" });
      setSdkError("Network error validating App ID");
    }
  }, []);

  // Enhanced Facebook SDK loading
  const loadFacebookSDK = useCallback(() => {
    console.log("Starting Facebook SDK load process...");
    setSdkError(null);

    // Validate App ID first
    validateAppId();

    // Set up fbAsyncInit BEFORE loading script
    window.fbAsyncInit = function () {
      console.log("fbAsyncInit called, initializing FB SDK...");
      try {
        window.FB.init({
          appId: CONFIG.APP_ID,
          cookie: true,
          xfbml: false,
          version: CONFIG.API_VERSION,
        });

        console.log("FB.init completed, checking status...");

        // Test the SDK immediately
        window.FB.getLoginStatus(function(response) {
          console.log("Initial FB login status:", response);
          setIsSDKLoaded(true);
          setSdkError(null);
          console.log("‚úÖ Facebook SDK fully initialized and tested");
        });

      } catch (error) {
        console.error("‚ùå FB.init failed:", error);
        setSdkError(`SDK initialization failed: ${error.message}`);
      }
    };

    // Check if FB SDK already exists
    if (window.FB && window.FB.init) {
      console.log("Facebook SDK already loaded, testing...");
      try {
        window.FB.getLoginStatus(function(response) {
          console.log("Existing FB SDK status:", response);
          setIsSDKLoaded(true);
          setSdkError(null);
        });
        return;
      } catch (error) {
        console.log("Existing FB SDK has issues, reloading...");
        window.FB = undefined;
      }
    }

    // Remove any existing script
    const existingScript = document.getElementById("facebook-jssdk");
    if (existingScript) {
      console.log("Removing existing FB SDK script...");
      existingScript.remove();
    }

    // Create and load new script
    console.log("Loading Facebook SDK script...");
    const script = document.createElement("script");
    script.id = "facebook-jssdk";
    script.async = true;
    script.defer = true;
    script.crossOrigin = "anonymous";
    script.src = "https://connect.facebook.net/en_US/sdk.js";

    script.onload = () => {
      console.log("‚úÖ Facebook SDK script loaded successfully");
    };

    script.onerror = (error) => {
      console.error("‚ùå Failed to load Facebook SDK script:", error);
      setSdkError("Failed to load Facebook SDK script. Check your internet connection or if Facebook is blocked.");
    };

    document.head.appendChild(script);

    // Timeout fallback
    setTimeout(() => {
      if (!isSDKLoaded) {
        console.warn("‚ö†Ô∏è Facebook SDK initialization timeout");
        setSdkError("Facebook SDK took too long to initialize. This might indicate:");
        setMessage("‚Ä¢ Your app is in Development mode\n‚Ä¢ Invalid App ID\n‚Ä¢ Network/firewall issues\n‚Ä¢ Facebook domains blocked");
      }
    }, 15000);

  }, [validateAppId, isSDKLoaded]);

  useEffect(() => {
    loadFacebookSDK();
  }, [loadFacebookSDK]);

  // Enhanced message listener
  useEffect(() => {
    const messageListener = (event) => {
      console.log("Received message event:", event);

      const validOrigins = [
        "https://www.facebook.com",
        "https://business.facebook.com",
      ];

      const isValidOrigin = validOrigins.some(origin => 
        event.origin === origin || event.origin.endsWith('.facebook.com')
      );

      if (!isValidOrigin) {
        console.log("Ignoring message from invalid origin:", event.origin);
        return;
      }

      try {
        const data = typeof event.data === "string" ? JSON.parse(event.data) : event.data;
        console.log("Parsed message data:", data);

        if (data?.type === "WA_EMBEDDED_SIGNUP") {
          console.log("üü¢ WA Embedded Signup event:", data);

          if (data.event === "FINISH") {
            console.log("‚úÖ Signup completed successfully:", data.data);
          } else if (data.event === "CANCEL") {
            console.log("‚ùå Signup cancelled:", data.data);
            setStatus("error");
            setMessage(`Signup cancelled at: ${data.data?.current_step || 'unknown step'}`);
          } else {
            console.log("‚ÑπÔ∏è Other signup event:", data.event);
          }
        }
      } catch (parseError) {
        console.log("Non-JSON message received:", event.data);
      }
    };

    window.addEventListener("message", messageListener);
    return () => window.removeEventListener("message", messageListener);
  }, []);

  const testFBConnection = () => {
    if (!window.FB) {
      alert("Facebook SDK not loaded");
      return;
    }

    console.log("Testing Facebook connection...");
    window.FB.getLoginStatus(function(response) {
      console.log("FB Connection Test Result:", response);
      alert(`FB Status: ${response.status}`);
    });
  };

  const startSignup = () => {
    if (!isSDKLoaded || !window.FB) {
      setMessage("Facebook SDK not ready. Please wait or refresh the page.");
      return;
    }

    console.log("üöÄ Starting WhatsApp Business signup...");
    setStatus("loading");
    setMessage("Opening WhatsApp Business signup...");

    const loginConfig = {
      config_id: CONFIG.CONFIG_ID,
      response_type: "code",
      override_default_response_type: true,
      extras: {
        setup: {},
        featureType: '',
        sessionInfoVersion: '3'
      }
    };

    console.log("FB.login config:", loginConfig);

    window.FB.login(
      (response) => {
        console.log("üì• FB.login response received:", response);

        if (response.error) {
          console.error("‚ùå FB.login error:", response.error);
          setStatus("error");
          setMessage(`Facebook login error: ${response.error.message || JSON.stringify(response.error)}`);
          return;
        }

        if (response.status === 'connected' && response.authResponse?.code) {
          const code = response.authResponse.code;
          console.log("‚úÖ Authorization code received, length:", code.length);
          setupBusinessAccount(code);
        } else {
          console.log("‚ùå No authorization code received. Full response:", response);
          setStatus("error");
          setMessage(`No authorization code received. Status: ${response.status}. This may indicate the signup was cancelled or the app configuration is incorrect.`);
        }
      },
      loginConfig
    );
  };

  const setupBusinessAccount = async (authCode) => {
    try {
      setMessage("Server is processing your WhatsApp Business setup...");
      console.log("üì§ Sending to backend with code length:", authCode.length);

      const requestPayload = {
        authorization_code: authCode,
        redirect_uri: CONFIG.REDIRECT_URI,
        client_info: {
          user_agent: navigator.userAgent,
          timestamp: new Date().toISOString(),
          app_validation: appValidation
        },
      };

      const res = await fetch(`${CONFIG.BACKEND_URL}/api/whatsapp/setup`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(requestPayload),
      });

      const result = await res.json();
      console.log("üì• Backend response:", result);

      if (result.success) {
        setStatus("success");
        setMessage(result.message || "WhatsApp Business setup completed.");
        setBusinessInfo(result.business_info || null);
        setNextSteps(result.next_steps || []);
      } else {
        throw new Error(result.error || `Server error: ${res.status}`);
      }
    } catch (err) {
      console.error("‚ùå Setup error:", err);
      setStatus("error");
      setMessage(`Setup failed: ${err.message}`);
    }
  };

  const reset = () => {
    setStatus("idle");
    setMessage("");
    setBusinessInfo(null);
    setNextSteps([]);
  };

  const forceReload = () => {
    window.location.reload();
  };

  return (
    <div style={{
      maxWidth: 720,
      margin: "40px auto",
      fontFamily: "system-ui, sans-serif",
      padding: "20px"
    }}>
      <h1>WhatsApp Business ‚Äî Embedded Signup</h1>

      {/* App Validation Status */}
      {appValidation && (
        <div style={{ 
          padding: "10px", 
          marginBottom: "20px", 
          borderRadius: "4px",
          backgroundColor: appValidation.valid ? "#d4edda" : "#f8d7da",
          border: `1px solid ${appValidation.valid ? "#c3e6cb" : "#f5c6cb"}`,
          color: appValidation.valid ? "#155724" : "#721c24"
        }}>
          <strong>App Validation:</strong> {appValidation.valid ? "‚úÖ Valid" : "‚ùå Invalid"}
          {appValidation.error && <div>Error: {appValidation.error}</div>}
        </div>
      )}

      {/* SDK Error Display */}
      {sdkError && (
        <div style={{
          backgroundColor: "#fff3cd",
          border: "1px solid #ffeaa7",
          borderRadius: "4px",
          padding: "10px",
          marginBottom: "20px",
          color: "#856404"
        }}>
          <strong>SDK Issue:</strong> {sdkError}
          <div style={{ marginTop: "8px" }}>
            <button onClick={forceReload} style={{ marginRight: "8px", padding: "4px 8px", fontSize: "12px" }}>
              Reload Page
            </button>
            <button onClick={testFBConnection} style={{ padding: "4px 8px", fontSize: "12px" }}>
              Test FB Connection
            </button>
          </div>
        </div>
      )}

      {/* Debug Info */}
      <div style={{ 
        fontSize: "12px", 
        color: "#666", 
        marginBottom: "20px", 
        padding: "10px", 
        backgroundColor: "#f5f5f5", 
        borderRadius: "4px",
        fontFamily: "monospace"
      }}>
        <strong>Debug Info:</strong><br/>
        SDK Loaded: {isSDKLoaded ? "‚úÖ" : "‚è≥"}<br/>
        FB Object: {window.FB ? "‚úÖ" : "‚ùå"}<br/>
        App ID: {CONFIG.APP_ID}<br/>
        Config ID: {CONFIG.CONFIG_ID}<br/>
        API Version: {CONFIG.API_VERSION}<br/>
        Redirect URI: {CONFIG.REDIRECT_URI}
      </div>

      {status === "idle" && (
        <>
          <button
            onClick={startSignup}
            disabled={!isSDKLoaded}
            style={{
              padding: "12px 20px",
              borderRadius: 8,
              border: "1px solid #25d366",
              backgroundColor: isSDKLoaded ? "#25d366" : "#ccc",
              color: "white",
              cursor: isSDKLoaded ? "pointer" : "not-allowed",
              fontSize: "16px",
              fontWeight: "600"
            }}
          >
            {isSDKLoaded ? "üöÄ Connect WhatsApp Business" : "‚è≥ Loading Facebook SDK..."}
          </button>
        </>
      )}

      {status === "loading" && (
        <div style={{ marginTop: 16 }}>
          <strong>Setting up your account‚Ä¶</strong>
          <p>{message}</p>
        </div>
      )}

      {status === "success" && (
        <div style={{ marginTop: 16 }}>
          <h3>‚úÖ Setup Complete</h3>
          <p>{message}</p>
          {businessInfo && (
            <div style={{
              marginTop: 12,
              padding: 16,
              border: "1px solid #25d366",
              borderRadius: 8,
              backgroundColor: "#f0fff4"
            }}>
              <div><strong>Business:</strong> {businessInfo.business_name}</div>
              <div><strong>WABA ID:</strong> {businessInfo.waba_id}</div>
              <div><strong>Phone Numbers:</strong> {businessInfo.phone_numbers?.length}</div>
            </div>
          )}
          <button onClick={reset} style={{ marginTop: 16, padding: "8px 16px" }}>
            üîÑ Setup Another Account
          </button>
        </div>
      )}

      {status === "error" && (
        <div style={{ marginTop: 16 }}>
          <h3 style={{ color: "#d73502" }}>‚ùå Setup Failed</h3>
          <div style={{ 
            padding: "12px", 
            backgroundColor: "#fff5f5", 
            border: "1px solid #fed7d7", 
            borderRadius: "6px",
            color: "#c53030",
            whiteSpace: "pre-line"
          }}>
            {message}
          </div>
          <button onClick={reset} style={{ marginTop: 12, padding: "8px 16px" }}>
            üîÑ Try Again
          </button>
        </div>
      )}
    </div>
  );
}
