import React, { useEffect, useState } from "react";

const CONFIG = {
  APP_ID: "1179716207111509", // Replace with your actual App ID
  API_VERSION: "v23.0",
  BACKEND_URL: "http://localhost:8081",
};

function App() {
  const [isSDKLoaded, setIsSDKLoaded] = useState(false);
  const [status, setStatus] = useState("idle"); // idle, loading, success, error
  const [message, setMessage] = useState("");
  const [businessInfo, setBusinessInfo] = useState(null);
  const [nextSteps, setNextSteps] = useState([]);

  useEffect(() => {
    // Minimal Facebook SDK initialization
    window.fbAsyncInit = function () {
      window.FB.init({
        appId: CONFIG.APP_ID,
        cookie: true,
        xfbml: true,
        version: CONFIG.API_VERSION,
      });
      setIsSDKLoaded(true);
    };

    // Event listener for messages (optional)
    const messageListener = (event) => {
      if (event.origin === "https://www.facebook.com") {
        try {
          const data =
            typeof event.data === "string"
              ? JSON.parse(event.data)
              : event.data;
          if (data.type === "WA_EMBEDDED_SIGNUP") {
            console.log("Embedded signup event:", data);
          }
        } catch (e) {
          // Ignore non-JSON messages
        }
      }
    };

    window.addEventListener("message", messageListener);
    return () => window.removeEventListener("message", messageListener);
  }, []);

  const startSignup = () => {
    if (!isSDKLoaded) {
      setMessage("Facebook SDK not ready. Please wait.");
      return;
    }

    setStatus("loading");
    setMessage("Opening WhatsApp Business signup...");

    // MINIMAL CLIENT OPERATION: Just get authorization code
    window.FB.login(
      (response) => {
        if (response.authResponse && response.authResponse.code) {
          // IMMEDIATELY send to server - no client processing
          setupBusinessAccount(response.authResponse.code);
        } else {
          setStatus("error");
          setMessage("Signup cancelled or failed");
        }
      },
      {
        response_type: "code",
        override_default_response_type: true,
        extras: {
          feature: "whatsapp_embedded_signup",
          setup_type: "BOTH",
        },
      },
    );
  };

  const setupBusinessAccount = async (authCode) => {
    try {
      setMessage("Server is processing your WhatsApp Business setup...");

      // SERVER HANDLES EVERYTHING: Token exchange, API calls, webhook setup, etc.
      const response = await fetch(`${CONFIG.BACKEND_URL}/api/whatsapp/setup`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          authorization_code: authCode,
          client_info: {
            user_agent: navigator.userAgent,
            ip_address: "client", // Server can get real IP
          },
        }),
      });

      const result = await response.json();

      if (result.success) {
        setStatus("success");
        setMessage(result.message);
        setBusinessInfo(result.business_info);
        setNextSteps(result.next_steps || []);
      } else {
        throw new Error(result.error);
      }
    } catch (error) {
      setStatus("error");
      setMessage(`Setup failed: ${error.message}`);
    }
  };

  const reset = () => {
    setStatus("idle");
    setMessage("");
    setBusinessInfo(null);
    setNextSteps([]);
  };

  return (
    <div className="container">
      <div className="header">
        <h1>üü¢ WhatsApp Business</h1>
        <p>Server-Optimized Embedded Signup</p>
      </div>

      <div className="content">
        {status === "idle" && (
          <>
            <p>
              Connect your WhatsApp Business account in seconds. Our server
              handles all the complex setup automatically.
            </p>
            <button
              className="btn"
              onClick={startSignup}
              disabled={!isSDKLoaded}
            >
              {isSDKLoaded ? "üöÄ Connect WhatsApp Business" : "‚è≥ Loading..."}
            </button>

            <div style={{ fontSize: "14px", color: "#666", marginTop: "20px" }}>
              <strong>What happens next:</strong>
              <ul style={{ paddingLeft: "20px", marginTop: "10px" }}>
                <li>Facebook login & business verification</li>
                <li>Automatic WhatsApp Business Account creation</li>
                <li>Phone number setup & verification</li>
                <li>Webhook configuration (server-side)</li>
                <li>Ready to send messages!</li>
              </ul>
            </div>
          </>
        )}

        {status === "loading" && (
          <div className="status loading">
            <div className="spinner"></div>
            <strong>Setting up your account...</strong>
            <p>{message}</p>
            <small>Please wait, this may take a moment.</small>
          </div>
        )}

        {status === "success" && (
          <>
            <div className="status success">
              <h2>‚úÖ Setup Complete!</h2>
              <p>{message}</p>
            </div>

            {businessInfo && (
              <div className="business-info">
                <h3>üìä Business Account Details</h3>
                <div className="info-item">
                  <span className="label">Business Name:</span>
                  <span className="value">
                    {businessInfo.business_name || "Not specified"}
                  </span>
                </div>
                <div className="info-item">
                  <span className="label">WABA ID:</span>
                  <span className="value">{businessInfo.waba_id}</span>
                </div>
                <div className="info-item">
                  <span className="label">Phone Numbers:</span>
                  <span className="value">
                    {businessInfo.phone_numbers?.length || 0}
                  </span>
                </div>
                <div className="info-item">
                  <span className="label">Webhooks:</span>
                  <span className="value">
                    {businessInfo.webhooks_enabled
                      ? "‚úÖ Enabled"
                      : "‚ùå Disabled"}
                  </span>
                </div>
                <div className="info-item">
                  <span className="label">Status:</span>
                  <span className="value">
                    {businessInfo.setup_complete
                      ? "‚úÖ Complete"
                      : "‚è≥ In Progress"}
                  </span>
                </div>

                {businessInfo.phone_numbers?.map((phone, index) => (
                  <div
                    key={phone.id}
                    style={{
                      marginTop: "15px",
                      paddingTop: "15px",
                      borderTop: "1px solid #eee",
                    }}
                  >
                    <strong>üì± Phone Number {index + 1}</strong>
                    <div className="info-item">
                      <span className="label">Number:</span>
                      <span className="value">{phone.phone_number}</span>
                    </div>
                    <div className="info-item">
                      <span className="label">Display Name:</span>
                      <span className="value">{phone.display_name}</span>
                    </div>
                    <div className="info-item">
                      <span className="label">Verified:</span>
                      <span className="value">
                        {phone.is_verified ? "‚úÖ Yes" : "‚ùå No"}
                      </span>
                    </div>
                    <div className="info-item">
                      <span className="label">Quality:</span>
                      <span className="value">
                        {phone.quality_rating || "Unknown"}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {nextSteps.length > 0 && (
              <div className="next-steps">
                <h3>üéØ Next Steps</h3>
                <ul>
                  {nextSteps.map((step, index) => (
                    <li key={index}>{step}</li>
                  ))}
                </ul>
              </div>
            )}

            <button className="btn" onClick={reset}>
              üîÑ Setup Another Account
            </button>
          </>
        )}

        {status === "error" && (
          <>
            <div className="status error">
              <h2>‚ùå Setup Failed</h2>
              <p>{message}</p>
            </div>
            <button className="btn" onClick={reset}>
              üîÑ Try Again
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
