import React, { useEffect, useState, useCallback } from "react";

const CONFIG = {
  APP_ID: "1187395139713463",
  API_VERSION: "v18.0", // ‚Üê Fixed: Use valid Graph API version
  CONFIG_ID: "2282335175549472",
  BACKEND_URL: "http://localhost:8081",
  REDIRECT_URI: "https://c6c766eee161.ngrok-free.app",
};

export default function App() {
  const [isSDKLoaded, setIsSDKLoaded] = useState(false);
  const [status, setStatus] = useState("idle");
  const [message, setMessage] = useState("");
  const [businessInfo, setBusinessInfo] = useState(null);
  const [nextSteps, setNextSteps] = useState([]);

  // Load Facebook SDK with proper error handling
  const loadFacebookSDK = useCallback(() => {
    // Set up the async init function BEFORE loading the script
    window.fbAsyncInit = function () {
      try {
        window.FB.init({
          appId: CONFIG.APP_ID,
          cookie: true,
          xfbml: false,
          version: CONFIG.API_VERSION,
        });
        setIsSDKLoaded(true);
        console.log("‚úÖ Facebook SDK initialized successfully");
      } catch (error) {
        console.error("‚ùå Facebook SDK initialization failed:", error);
        setMessage(
          "Facebook SDK initialization failed. Please refresh the page.",
        );
      }
    };

    // Check if SDK is already loaded
    if (window.FB) {
      console.log("‚úÖ Facebook SDK already exists");
      setIsSDKLoaded(true);
      return;
    }

    // Check if script is already in DOM
    if (document.getElementById("facebook-jssdk")) {
      console.log("‚è≥ Facebook SDK script already exists, waiting for init...");
      return;
    }

    console.log("üì• Loading Facebook SDK...");
    const js = document.createElement("script");
    js.id = "facebook-jssdk";
    js.src = "https://connect.facebook.net/en_US/sdk.js";
    js.async = true;
    js.defer = true;

    js.onload = () => {
      console.log("‚úÖ Facebook SDK script loaded successfully");
    };

    js.onerror = (error) => {
      console.error("‚ùå Failed to load Facebook SDK script:", error);
      setMessage(
        "Failed to load Facebook SDK. Please check your internet connection and try again.",
      );
    };

    document.head.appendChild(js);

    // Fallback timeout
    setTimeout(() => {
      if (!window.FB) {
        console.warn("‚ö†Ô∏è Facebook SDK taking too long to initialize");
        setMessage(
          "Facebook SDK is taking longer than expected to load. Please refresh the page.",
        );
      }
    }, 10000);
  }, []); // Empty dependency array since this function doesn't depend on any state

  useEffect(() => {
    loadFacebookSDK();
  }, [loadFacebookSDK]); // Now loadFacebookSDK is in the dependency array

  // Listen for Embedded Signup events
  useEffect(() => {
    const messageListener = (event) => {
      const ok =
        typeof event.origin === "string" &&
        (event.origin.endsWith(".facebook.com") ||
          event.origin === "https://www.facebook.com" ||
          event.origin === "https://business.facebook.com");
      if (!ok) return;

      try {
        const data =
          typeof event.data === "string" ? JSON.parse(event.data) : event.data;
        if (data?.type === "WA_EMBEDDED_SIGNUP") {
          console.log("WA Embedded Signup event:", data);

          if (data.event === "FINISH") {
            console.log("Signup completed:", {
              waba_id: data.data?.waba_id,
              phone_number_id: data.data?.phone_number_id,
              business_id: data.data?.business_id,
            });
          } else if (data.event === "CANCEL") {
            console.log("Signup cancelled at screen:", data.data?.current_step);
            setStatus("error");
            setMessage("Signup was cancelled or encountered an error");
          }
        }
      } catch {
        // ignore non-JSON
      }
    };

    window.addEventListener("message", messageListener);
    return () => window.removeEventListener("message", messageListener);
  }, []);

  const startSignup = () => {
    if (!isSDKLoaded) {
      setMessage("Facebook SDK not ready. Please wait.");
      return;
    }

    setStatus("loading");
    setMessage("Opening WhatsApp Business signup...");

    window.FB.login(
      (response) => {
        console.log("FB.login response:", response);

        if (response.error) {
          setStatus("error");
          setMessage(`Facebook login error: ${response.error.message}`);
          return;
        }

        const code = response?.authResponse?.code;
        if (code) {
          console.log(
            "Received authorization code, proceeding with backend setup...",
          );
          setupBusinessAccount(code);
        } else {
          console.log("No code received. Response:", response);
          setStatus("error");
          setMessage(
            "Signup cancelled or failed. No authorization code received.",
          );
        }
      },
      {
        config_id: CONFIG.CONFIG_ID,
        response_type: "code",
        override_default_response_type: true,
        extras: {
          setup: {},
          featureType: "",
          sessionInfoVersion: "3",
        },
      },
    );
  };

  const setupBusinessAccount = async (authCode) => {
    try {
      setMessage("Server is processing your WhatsApp Business setup...");

      const requestPayload = {
        authorization_code: authCode,
        redirect_uri: CONFIG.REDIRECT_URI,
        client_info: {
          user_agent: navigator.userAgent,
          timestamp: new Date().toISOString(),
        },
      };

      console.log("Sending to backend:", requestPayload);

      const res = await fetch(`${CONFIG.BACKEND_URL}/api/whatsapp/setup`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(requestPayload),
      });

      const result = await res.json();
      console.log("Backend response:", result);

      if (result.success) {
        setStatus("success");
        setMessage(result.message || "WhatsApp Business setup completed.");
        setBusinessInfo(result.business_info || null);
        setNextSteps(result.next_steps || []);
      } else {
        throw new Error(result.error || "Unknown setup error");
      }
    } catch (err) {
      console.error("Setup error:", err);
      setStatus("error");
      setMessage(`Setup failed: ${err.message}`);
    }
  };

  const reset = () => {
    setStatus("idle");
    setMessage("");
    setBusinessInfo(null);
    setNextSteps([]);
  };

  return (
    <div
      style={{
        maxWidth: 720,
        margin: "40px auto",
        fontFamily: "system-ui, sans-serif",
        padding: "20px",
      }}
    >
      <h1>WhatsApp Business ‚Äî Embedded Signup</h1>
      <p style={{ color: "#555", marginBottom: "20px" }}>
        Click the button to connect your WhatsApp Business account. The server
        completes the setup automatically.
      </p>

      {/* Debug Info */}
      <div
        style={{
          fontSize: "12px",
          color: "#666",
          marginBottom: "20px",
          padding: "10px",
          backgroundColor: "#f5f5f5",
          borderRadius: "4px",
        }}
      >
        <strong>Debug Info:</strong>
        <br />
        SDK Loaded: {isSDKLoaded ? "‚úÖ" : "‚è≥"}
        <br />
        Config ID: {CONFIG.CONFIG_ID}
        <br />
        Redirect URI: {CONFIG.REDIRECT_URI}
        <br />
        FB Available: {window.FB ? "‚úÖ" : "‚ùå"}
      </div>

      {status === "idle" && (
        <>
          <button
            onClick={startSignup}
            disabled={!isSDKLoaded}
            style={{
              padding: "12px 20px",
              borderRadius: 8,
              border: "1px solid #25d366",
              backgroundColor: isSDKLoaded ? "#25d366" : "#ccc",
              color: "white",
              cursor: isSDKLoaded ? "pointer" : "not-allowed",
              fontSize: "16px",
              fontWeight: "600",
            }}
          >
            {isSDKLoaded
              ? "üöÄ Connect WhatsApp Business"
              : "‚è≥ Loading Facebook SDK..."}
          </button>
          <div style={{ marginTop: 16, fontSize: 14, color: "#666" }}>
            <strong>Flow:</strong> Login ‚Üí Embedded Signup ‚Üí Code ‚Üí Server
            Exchange ‚Üí WABA/Phone setup
          </div>
        </>
      )}

      {status === "loading" && (
        <div style={{ marginTop: 16 }}>
          <strong>Setting up your account‚Ä¶</strong>
          <p>{message}</p>
          <div
            style={{
              width: "100%",
              height: "4px",
              backgroundColor: "#eee",
              borderRadius: "2px",
              marginTop: "10px",
            }}
          >
            <div
              style={{
                width: "50%",
                height: "100%",
                backgroundColor: "#25d366",
                borderRadius: "2px",
                animation: "pulse 1.5s ease-in-out infinite",
              }}
            ></div>
          </div>
        </div>
      )}

      {status === "success" && (
        <div style={{ marginTop: 16 }}>
          <h3>‚úÖ Setup Complete</h3>
          <p>{message}</p>

          {businessInfo && (
            <div
              style={{
                marginTop: 12,
                padding: 16,
                border: "1px solid #25d366",
                borderRadius: 8,
                backgroundColor: "#f0fff4",
              }}
            >
              <div style={{ marginBottom: "8px" }}>
                <strong>Business Name:</strong>{" "}
                {businessInfo.business_name || "‚Äî"}
              </div>
              <div style={{ marginBottom: "8px" }}>
                <strong>WABA ID:</strong> {businessInfo.waba_id || "‚Äî"}
              </div>
              <div style={{ marginBottom: "8px" }}>
                <strong>Phone Numbers:</strong>{" "}
                {businessInfo.phone_numbers?.length ?? 0}
              </div>
              <div style={{ marginBottom: "8px" }}>
                <strong>Webhooks:</strong>{" "}
                {businessInfo.webhooks_enabled ? "‚úÖ Enabled" : "‚ùå Disabled"}
              </div>
              <div style={{ marginBottom: "8px" }}>
                <strong>Status:</strong>{" "}
                {businessInfo.setup_complete ? "‚úÖ Complete" : "‚è≥ In Progress"}
              </div>

              {(businessInfo.phone_numbers || []).map((p, i) => (
                <div
                  key={p.id || i}
                  style={{
                    marginTop: 12,
                    paddingTop: 12,
                    borderTop: "1px solid #e0e0e0",
                  }}
                >
                  <strong>üì± Phone {i + 1}</strong>
                  <div>
                    <strong>Number:</strong> {p.phone_number}
                  </div>
                  <div>
                    <strong>Display Name:</strong> {p.display_name}
                  </div>
                  <div>
                    <strong>Verified:</strong>{" "}
                    {p.is_verified ? "‚úÖ Yes" : "‚ùå No"}
                  </div>
                  <div>
                    <strong>Quality:</strong> {p.quality_rating || "Unknown"}
                  </div>
                </div>
              ))}
            </div>
          )}

          {nextSteps?.length > 0 && (
            <div style={{ marginTop: 16 }}>
              <h4>üéØ Next Steps</h4>
              <ul style={{ paddingLeft: "20px" }}>
                {nextSteps.map((step, i) => (
                  <li key={i} style={{ marginBottom: "4px" }}>
                    {step}
                  </li>
                ))}
              </ul>
            </div>
          )}

          <button
            onClick={reset}
            style={{
              marginTop: 16,
              padding: "8px 16px",
              borderRadius: 6,
              border: "1px solid #ccc",
              backgroundColor: "white",
              cursor: "pointer",
            }}
          >
            üîÑ Setup Another Account
          </button>
        </div>
      )}

      {status === "error" && (
        <div style={{ marginTop: 16 }}>
          <h3 style={{ color: "#d73502" }}>‚ùå Setup Failed</h3>
          <div
            style={{
              padding: "12px",
              backgroundColor: "#fff5f5",
              border: "1px solid #fed7d7",
              borderRadius: "6px",
              color: "#c53030",
            }}
          >
            {message}
          </div>
          <button
            onClick={reset}
            style={{
              marginTop: 12,
              padding: "8px 16px",
              borderRadius: 6,
              border: "1px solid #ccc",
              backgroundColor: "white",
              cursor: "pointer",
            }}
          >
            üîÑ Try Again
          </button>
        </div>
      )}

      <style jsx>{`
        @keyframes pulse {
          0%,
          100% {
            opacity: 1;
          }
          50% {
            opacity: 0.5;
          }
        }
      `}</style>
    </div>
  );
}
