import React, { useEffect, useState } from "react";

const CONFIG = {
  APP_ID: "1179716207111509",           // <- your App ID
  API_VERSION: "v23.0",                 // <- valid Graph/SDK version
  CONFIG_ID: "2282335175549472",        // <- your Embedded Signup config_id
  BACKEND_URL: "http://localhost:8081",
  REDIRECT_URI: "http://localhost:8081/meta/wa/callback", // must EXACTLY match dashboard
};

export default function App() {
  const [sdkState, setSdkState] = useState({ ready: false, error: "" });
  const [status, setStatus] = useState("idle"); // idle | loading | success | error
  const [message, setMessage] = useState("");
  const [businessInfo, setBusinessInfo] = useState(null);
  const [nextSteps, setNextSteps] = useState([]);

  // --- Robust SDK loader that does NOT rely on window.fbAsyncInit ---
  useEffect(() => {
    const scriptId = "facebook-jssdk";

    // If FB is already present and has init'd API, mark ready
    if (window.FB && typeof window.FB.getLoginStatus === "function") {
      setSdkState({ ready: true, error: "" });
      return;
    }

    // Clean any stale script tag
    const existing = document.getElementById(scriptId);
    if (existing) existing.remove();

    // Create script tag
    const js = document.createElement("script");
    js.id = scriptId;
    js.async = true;
    js.defer = true;
    js.crossOrigin = "anonymous";
    js.src = "https://connect.facebook.net/en_US/sdk.js";

    js.onload = () => {
      try {
        if (!window.FB) {
          setSdkState({
            ready: false,
            error:
              "Facebook SDK loaded but window.FB is undefined. Check blockers/CSP.",
          });
          return;
        }
        // Initialize SDK *after* script load
        window.FB.init({
          appId: CONFIG.APP_ID,
          cookie: true,
          xfbml: false,
          version: CONFIG.API_VERSION,
        });

        // Sanity call to confirm SDK is functional
        window.FB.getLoginStatus(() => {
          setSdkState({ ready: true, error: "" });
          console.log("‚úÖ FB SDK initialized:", CONFIG.API_VERSION);
        });
      } catch (e) {
        console.error("FB.init error:", e);
        setSdkState({
          ready: false,
          error: `FB.init failed: ${e?.message || String(e)}`,
        });
      }
    };

    js.onerror = () => {
      setSdkState({
        ready: false,
        error:
          "Failed to load Facebook SDK. Check network/ad-blockers and CSP allowing connect.facebook.net.",
      });
    };

    document.head.appendChild(js);

    // Failsafe timeout (e.g., adblock)
    const t = setTimeout(() => {
      if (!sdkState.ready) {
        setSdkState((s) =>
          s.ready
            ? s
            : {
                ready: false,
                error:
                  "Facebook SDK took too long to initialize. Verify App ID/version and that the script isn't blocked.",
              }
        );
      }
    }, 15000);

    return () => clearTimeout(t);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Optional: listen to Embedded Signup postMessage events
  useEffect(() => {
    const onMessage = (event) => {
      const ok =
        event.origin === "https://www.facebook.com" ||
        event.origin === "https://business.facebook.com" ||
        (typeof event.origin === "string" &&
          event.origin.endsWith(".facebook.com"));
      if (!ok) return;

      try {
        const data =
          typeof event.data === "string" ? JSON.parse(event.data) : event.data;
        if (data?.type === "WA_EMBEDDED_SIGNUP") {
          console.log("WA Embedded Signup event:", data);
          if (data.event === "CANCEL") {
            setStatus("error");
            setMessage(
              `Signup cancelled${
                data?.data?.current_step ? ` at ${data.data.current_step}` : ""
              }`
            );
          }
        }
      } catch {
        /* ignore */
      }
    };
    window.addEventListener("message", onMessage);
    return () => window.removeEventListener("message", onMessage);
  }, []);

  const startSignup = () => {
    if (!window.FB || !sdkState.ready) {
      setMessage("Facebook SDK not ready. If this persists, disable ad-blockers.");
      return;
    }

    setStatus("loading");
    setMessage("Opening WhatsApp Business signup...");

    const loginConfig = {
      config_id: CONFIG.CONFIG_ID, // REQUIRED for Embedded Signup
      response_type: "code", // REQUIRED to get exchangeable code
      override_default_response_type: true, // REQUIRED
      // extras: { setup: { setup_type: "BOTH" } } // optional prefill
    };

    console.log("FB.login config:", loginConfig);

    window.FB.login(
      (response) => {
        console.log("FB.login response:", response);

        if (response?.error) {
          setStatus("error");
          setMessage(
            `FB.login error: ${response.error?.message || JSON.stringify(response.error)}`
          );
          return;
        }

        const code = response?.authResponse?.code;
        if (response?.status === "connected" && code) {
          setupBusinessAccount(code);
        } else {
          setStatus("error");
          setMessage(
            `No authorization code received (status: ${
              response?.status || "unknown"
            }). Check app/config_id and permissions.`
          );
        }
      },
      loginConfig
    );
  };

  const setupBusinessAccount = async (authCode) => {
    try {
      setMessage("Server is processing your WhatsApp Business setup...");

      const res = await fetch(`${CONFIG.BACKEND_URL}/api/whatsapp/setup`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          authorization_code: authCode,
          redirect_uri: CONFIG.REDIRECT_URI, // must EXACTLY match dashboard value
          client_info: { user_agent: navigator.userAgent, ts: Date.now() },
        }),
      });

      const result = await res.json();
      if (result.success) {
        setStatus("success");
        setMessage(result.message || "WhatsApp Business setup completed.");
        setBusinessInfo(result.business_info || null);
        setNextSteps(result.next_steps || []);
      } else {
        throw new Error(result.error || `Server error (${res.status})`);
      }
    } catch (err) {
      setStatus("error");
      setMessage(`Setup failed: ${err.message}`);
    }
  };

  const reset = () => {
    setStatus("idle");
    setMessage("");
    setBusinessInfo(null);
    setNextSteps([]);
  };

  return (
    <div style={{ maxWidth: 720, margin: "40px auto", fontFamily: "system-ui, sans-serif" }}>
      <h1>WhatsApp Business ‚Äî Embedded Signup</h1>

      <div
        style={{
          fontSize: 12,
          color: "#666",
          marginBottom: 16,
          padding: 10,
          background: "#f6f7f8",
          borderRadius: 6,
          fontFamily: "monospace",
        }}
      >
        SDK Ready: {sdkState.ready ? "‚úÖ" : "‚è≥"} | FB Object: {window.FB ? "‚úÖ" : "‚ùå"}
        <br />
        App ID: {CONFIG.APP_ID} | Config ID: {CONFIG.CONFIG_ID} | Version: {CONFIG.API_VERSION}
        <br />
        Redirect URI: {CONFIG.REDIRECT_URI}
        {sdkState.error && (
          <>
            <br />
            <span style={{ color: "#b00020" }}>SDK Error: {sdkState.error}</span>
          </>
        )}
      </div>

      {status === "idle" && (
        <button
          onClick={startSignup}
          disabled={!sdkState.ready}
          style={{
            padding: "12px 20px",
            borderRadius: 8,
            border: "1px solid #25d366",
            backgroundColor: sdkState.ready ? "#25d366" : "#ccc",
            color: "#fff",
            fontWeight: 600,
            cursor: sdkState.ready ? "pointer" : "not-allowed",
          }}
        >
          {sdkState.ready ? "üöÄ Connect WhatsApp Business" : "‚è≥ Loading Facebook SDK..."}
        </button>
      )}

      {status === "loading" && (
        <div style={{ marginTop: 16 }}>
          <strong>Setting up your account‚Ä¶</strong>
          <p>{message}</p>
        </div>
      )}

      {status === "success" && (
        <div style={{ marginTop: 16 }}>
          <h3>‚úÖ Setup Complete</h3>
          <p>{message}</p>
          {businessInfo && (
            <div style={{ marginTop: 12, padding: 12, border: "1px solid #25d366", borderRadius: 8 }}>
              <div><strong>Business:</strong> {businessInfo.business_name}</div>
              <div><strong>WABA ID:</strong> {businessInfo.waba_id}</div>
              <div><strong>Phone Numbers:</strong> {businessInfo.phone_numbers?.length ?? 0}</div>
            </div>
          )}
          {nextSteps?.length > 0 && (
            <div style={{ marginTop: 16 }}>
              <h4>Next Steps</h4>
              <ul style={{ paddingLeft: 20 }}>
                {nextSteps.map((s, i) => (
                  <li key={i}>{s}</li>
                ))}
              </ul>
            </div>
          )}
          <button onClick={reset} style={{ marginTop: 12, padding: "8px 12px", borderRadius: 8 }}>
            üîÑ Setup Another Account
          </button>
        </div>
      )}

      {status === "error" && (
        <div style={{ marginTop: 16 }}>
          <h3 style={{ color: "#d73502" }}>‚ùå Setup Failed</h3>
          <div
            style={{
              padding: 12,
              backgroundColor: "#fff5f5",
              border: "1px solid #fed7d7",
              borderRadius: 6,
              color: "#c53030",
              whiteSpace: "pre-line",
            }}
          >
            {message}
          </div>
          <button onClick={reset} style={{ marginTop: 12, padding: "8px 12px", borderRadius: 8 }}>
            üîÑ Try Again
          </button>
        </div>
      )}
    </div>
  );
}
